import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import Note from "./Note/Note.jsx";
import NoteForm from "./NoteForm/NoteForm.jsx";

import { DB_config } from 'src/config/config.js'
import firebase from 'firbase/app'

class App extends Component {
  constructor(props) {
    super(props);
    //setting up react state to our components
    this.addNote = this.addNote.bind(this);
    this.app = firebase.initializaApp(DB_Config)
    this.db = this.app.database().ref().child('notes')

    this.state = {
      notes: [],
    };
  }
  addNote(note) {
    const previousNotes = this.state.notes;
    previousNotes.push({ id: previousNotes.length + 1, noteContent: note });

    this.setState({
      notes: previousNotes
    });
  }

  render() {
    return (
      <div className="notesWrapper">
        <div className="notesHeader">
          <div className="heading">Hola Code Notes</div>
        </div>
        <div className="notesBody">
          {this.state.notes.map(note => {
            return (
              <Note
                noteContent={note.noteContent}
                noteId={note.id}
                key={note.id}
              />
            );
          })}
        </div>
        <div className="notesFooter">
          <NoteForm addNote={this.addNote} />
        </div>
      </div>
    );
  }
}

export default App;
